def pylint_is_happy(s):
{% if QUESTION.parameters.isfunction or not (QUESTION.parameters.isfunction is defined) %}
    if len(s.strip()) == 0 or s.strip()[0] not in ['"', "'"]:
        s = "'''Dummy module docstring'''\n" + s  # Add module docstring if nec.
{% endif %}
    try:
