        source.write(s)
        source.close()
        env = os.environ.copy()
        env['HOME'] = os.getcwd()
        pylint_opts = ['--disable=C0303,C0330,R0903', '--enable=C0326']
{% if QUESTION.parameters.allowglobals %}
